#!/usr/bin/env node
import fs from 'fs';
import path from 'path';

const projectRoot = process.cwd();
const stylesFolder = path.join(projectRoot, 'styles');

if (!fs.existsSync(stylesFolder)) fs.mkdirSync(stylesFolder, { recursive: true });

const copyFile = (fileName) => {
  const src = path.join(projectRoot, 'node_modules', 'jay-stack-ui', 'styles', fileName);
  const dest = path.join(stylesFolder, fileName);

  if (!fs.existsSync(dest)) {
    fs.copyFileSync(src, dest);
    console.log(`âœ… ${fileName} copied to project styles folder`);
  } else {
    console.log(`âš  ${fileName} already exists in project styles folder, skipping`);
  }
};

// Copy both classes.css and colors.js
copyFile('classes.css');
copyFile('colors.js');

console.log('ðŸŽ‰ Jay Stack UI initialized! Run ');
